import "./App.css";

function App() {
  const [info, setInfo] = useState([]);
  const sddItemHandeler = function (item) {
    setInfo((it) => {
      return [...it, item];
    });
  };
  console.log(info);
  return (
    <>
      <Titr />
      <Form onAddItem={sddItemHandeler} />
      <Itemrapper ItemInfo={info} />
    </>
  );
}

