const Form = function (props) {
  const [TextState, setTextState] = useState("");
  const [NumberState, setNumber] = useState(1);
  const Texthandeller = function (event) {
    setTextState(event.target.value);
  };
  const Numberhandeller = function (event) {
    const newValue = Number(event.target.value);
    setNumber(newValue >= 1 ? newValue : 1);
    event.target.value = 1;
  };
  const setinfo = function (event) {
    event.preventDefault();
    const inf = {
      title: TextState,
      quantity: NumberState,
      done: false,
      id: Date.now(),
    };
    setTextState("");
    setNumber(1);
    props.onAddItem(inf);
  };
  return (
    <>
      <form className="form" onSubmit={(event) => setinfo(event)}>
        <h className="form_titr">WHAT DO YOU NEED FOR YOUR TRIP ?</h>
        <input
          className="input_number"
          value={NumberState}
          onChange={(event) => Numberhandeller(event)}
          type="number"
          placeholder="1"
        />
        <input
          value={TextState}
          className="input_text"
          type="text"
          onChange={(event) => Texthandeller(event)}
          placeholder="item..."
        />
        <Btn txt="Add" />
      </form>
    </>
  );
};
const Btn = function (props) {
  return (
    <button type="submit" className="btn_style">
      {props.txt}
    </button>
  );
};
const Itemrapper = function (items) {
  {
    console.log(items);
  }
  return (
    <>
      <div className="itemList">
        {items
          ? items.ItemInfo.map((it) => {
              return <Item txt={it.title} key={it.id} />;
            })
          : ""}
      </div>
    </>
  );
};
